
public class ForTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

		for(int i = 9; i > 4; i--) {
			System.out.print(i+"  ");	
		}
		System.out.println();
		for(int i = 4; i > -1; i--) {
			System.out.print(i+"  ");
		}
		System.out.println();
		for(int n = -1; n > -6; n--) {
			System.out.print(n+"  ");
		}

	}

}
